import React, { useState, useEffect } from 'react';
import AddNewCar from './AddNewCar';
import UpdateCar from './UpdateCar';
import './GetAllCars.css';

export default function GetAllCars() {
  const [cars, setCars] = useState([]);
  const [editCar, setEditCar] = useState(null);
  const [count, setCount] = useState(0);

  useEffect(() => {
    fetch('http://localhost:5000/cars')
      .then((response) => response.json())
      .then((data) => setCars(data))
      .catch((error) => console.error('Hiba:', error));
  }, [count]);

  const handleCount = () => setCount(count + 1);
  const handleEdit = (car) => setEditCar(car);
  const cancelEdit = () => setEditCar(null);

  return (
    <div className="get-all-cars-container">
      <h2>Autók listája</h2>
      {editCar ? (
        <UpdateCar carObj={editCar} handleCount={handleCount} cancelEdit={cancelEdit} />
      ) : (
        <AddNewCar handleCount={handleCount} />
      )}

      <div className="car-list">
        {cars.map((car) => (
          <div key={car.id} className="car-card">
            <h3>{car.brand} - {car.type}</h3>
            <p>Szín: {car.color}</p>
            <p>Gyártási év: {car.myear}</p>
            <button className="update-button" onClick={() => handleEdit(car)}>Szerkesztés</button>
          </div>
        ))}
      </div>
    </div>
  );
}
