import React, {useState, useEffect} from 'react'
import DeleteCar from './DeleteCar'
import AddNewCar from './AddNewCar'
import './GetAllCars.css' // Importáljuk a CSS fájlt

export default function GetAllCars(props) {

  const url = `http://localhost:5081/cars`
  const [carsData, setCarsData] = useState([])
  const [carObj, setCarObj] = useState(null)

  useEffect(() => {
    (async () => {
      const request = await fetch(url, {
        headers: {
          'Content-Type': 'application/json'
        }
      })
      if (!request.ok) {
        console.log("error")
        return
      }
      const response = await request.json();
      setCarsData(response.result)
      console.log(response.message)
    })()
  }, [props.count, url])

const handleCarObj = (carFromCard) =>
{
  setCarObj(carFromCard)
}

  const carElements = carsData.map(
    car =>
    {
    return (
      <div onDoubleClick={() => handleCarObj(car)} className="car-card" key={car.id}>
        <h3>{car.brand}</h3>
        <p>{car.type}</p>
        <div className="car-details">
          <span>Szín: {car.color}</span>
          <span>Évjárat: {car.myear}</span>
        </div>
        <div className="car-actions">
          <DeleteCar carId={car.id} handleCount={props.handleCount}/>
        </div>
      </div>
    )
    }
)

return(
  <div className="get-all-cars-container">
    <AddNewCar handleCount={props.handleCount} carObj={carObj || {}}/>

    <div className="car-list">{carElements}</div>
  </div>
)
}
