import React from "react";
import './GetAllCars'; // Importáljuk a CSS fájlt

export default function DeleteCar(props) {
  const handleCarId = async () => {
    const url = `http://localhost:5081/Cars/CarById?id=${props.carId}`;

    try {
      const request = await fetch(url, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (!request.ok) {
        console.error(`Hiba a törlés során (${request.status}): ${request.statusText}`);
        return;
      }

      const response = await request.json();
      props.handleCount();
      console.log(response.message);
    } catch (error) {
      console.error('Hiba történt a törlés kérésekor:', error);
    }
  };

  return (
    <button className="delete-button" onClick={handleCarId}>
      Töröl
    </button>
  );
}
