import React, { useState, useEffect } from 'react';
import DeleteCar from './DeleteCar';
import AddNewCar from './AddNewCar';
import UpdateCar from './UpdateCar'; // Lehet, hogy erre külön komponens lesz

export default function GetAllCars(props) {
    const url = `http://localhost:5081/cars`;
    const [carsData, setCarsData] = useState([]);
    const [carObjToUpdate, setCarObjToUpdate] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchCars = async () => {
            setLoading(true);
            setError(null);
            try {
                const request = await fetch(url, {
                    headers: {
                        'Content-Type': 'application/json',
                    },
                });
                if (!request.ok) {
                    throw new Error(`HTTP error! status: ${request.status}`);
                }
                const response = await request.json();
                setCarsData(response.result);
                console.log(response.message);
            } catch (e) {
                setError(e.message);
                console.error("Error fetching cars:", e);
            } finally {
                setLoading(false);
            }
        };

        fetchCars();
    }, [props.count]); // Függőségben maradhat, ha a szülő frissítése szükséges

    const handleCarObj = (carFromCard) => {
        setCarObjToUpdate(carFromCard);
    };

    const carElements = carsData.map((car) => (
        <div
            onDoubleClick={() => handleCarObj(car)}
            className='card m3 pt-2'
            key={car.id}
            style={{ width: 200, float: 'left' }} // Ezt később CSS-sel érdemesebb kezelni
        >
            <h3>{car.brand}</h3>
            <h3>{car.type}</h3>
            <h3>{car.color}</h3>
            <h3>{car.myear}</h3>
            <div>
                <DeleteCar carId={car.id} handleCount={props.handleCount} />
            </div>
        </div>
    ));

    return (
        <>
            <AddNewCar handleCount={props.handleCount} carObj={carObjToUpdate || {}} />

            {loading && <p>Loading cars...</p>}
            {error && <p>Error loading cars: {error}</p>}
            <div>{carElements}</div>
        </>
    );
}
