import React, { useState, useEffect } from 'react';
import './GetAllCars.css';

export default function AddNewCar({ carToUpdate, onCarAdded, onUpdateComplete }) {
  const [carData, setCarData] = useState({
    brand: '',
    type: '',
    color: '',
    myear: '',
  });
  const [isUpdateMode, setIsUpdateMode] = useState(false);

  useEffect(() => {
    if (carToUpdate) {
      setIsUpdateMode(true);
      setCarData({
        brand: carToUpdate.brand || '',
        type: carToUpdate.type || '',
        color: carToUpdate.color || '',
        myear: carToUpdate.myear
          ? new Date(carToUpdate.myear).toISOString().split('T')[0]
          : '',
      });
    } else {
      setIsUpdateMode(false);
      setCarData({ brand: '', type: '', color: '', myear: '' });
    }
  }, [carToUpdate]);

  const handleChange = (event) => {
    const { name, value } = event.target;
    setCarData({ ...carData, [name]: value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const url = `http://localhost:5000/cars${isUpdateMode ? `/${carToUpdate.id}` : ''}`;
    const method = isUpdateMode ? 'PUT' : 'POST';

    try {
      const response = await fetch(url, {
        method: method,
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(carData),
      });

      if (!response.ok) {
        console.error(`Hiba a kérés során (${response.status}): ${response.statusText}`);
        return;
      }

      const data = await response.json();
      console.log("Sikeres művelet:", data);

      if (isUpdateMode) {
        onUpdateComplete(); // Szerkesztés után reset
      } else {
        onCarAdded(); // Új autó esetén frissítés
      }

      setCarData({ brand: '', type: '', color: '', myear: '' }); // Form ürítése
    } catch (error) {
      console.error('Hiba történt a kérés során:', error);
    }
  };

  return (
    <div className="add-new-car-container">
      <h2>{isUpdateMode ? 'Autó szerkesztése' : 'Új autó hozzáadása'}</h2>
      <form onSubmit={handleSubmit} className="car-form">
        <div className="form-group">
          <label htmlFor="brand">Márka</label>
          <input type="text" id="brand" name="brand" value={carData.brand} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label htmlFor="type">Típus</label>
          <input type="text" id="type" name="type" value={carData.type} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label htmlFor="color">Szín</label>
          <input type="text" id="color" name="color" value={carData.color} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label htmlFor="myear">Gyártási év</label>
          <input type="date" id="myear" name="myear" value={carData.myear} onChange={handleChange} required />
        </div>
        <button type="submit" className="btn btn-primary">
          {isUpdateMode ? 'Mentés' : 'Hozzáadás'}
        </button>
        {isUpdateMode && (
          <button type="button" className="btn btn-secondary" onClick={onUpdateComplete}>
            Mégse
          </button>
        )}
      </form>
    </div>
  );
}
