// components/UpdateCar.js
import React, { useState, useEffect } from 'react';
import './GetAllCars.css';

export default function UpdateCar(props) {
  const [carData, setCarData] = useState({
    brand: '',
    type: '',
    color: '',
    myear: '',
  });

  useEffect(() => {
    if (props.car) {
      setCarData({
        brand: props.car.brand || '',
        type: props.car.type || '',
        color: props.car.color || '',
        myear: props.car.myear ? new Date(props.car.myear).toISOString().split('T')[0] : '',
      });
    }
  }, [props.car]);

  const handleChange = (event) => {
    const { name, value } = event.target;
    setCarData({ ...carData, [name]: value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const url = `http://localhost:5000/cars/${props.car._id}`;

    try {
      const request = await fetch(url, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(carData),
      });

      if (!request.ok) {
        console.error(`Hiba a módosítás során (${request.status}): ${request.statusText}`);
        return;
      }

      console.log("Autó sikeresen módosítva.");
      props.onUpdateComplete(); // Notify parent component
    } catch (error) {
      console.error('Hiba történt a módosítás kérésekor:', error);
    }
  };

  return (
    <div className="update-car-container">
      <h2>Autó szerkesztése</h2>
      <form onSubmit={handleSubmit} className="car-form">
        <div className="form-group">
          <label htmlFor="brand">Márka</label>
          <input type="text" id="brand" name="brand" value={carData.brand} onChange={handleChange} className="form-control" required />
        </div>
        <div className="form-group">
          <label htmlFor="type">Típus</label>
          <input type="text" id="type" name="type" value={carData.type} onChange={handleChange} className="form-control" required />
        </div>
        <div className="form-group">
          <label htmlFor="color">Szín</label>
          <input type="text" id="color" name="color" value={carData.color} onChange={handleChange} className="form-control" required />
        </div>
        <div className="form-group">
          <label htmlFor="myear">Gyártási év</label>
          <input type="date" id="myear" name="myear" value={carData.myear} onChange={handleChange} className="form-control" required />
        </div>
        <button type="submit" className="btn btn-warning">Mentés</button>
      </form>
    </div>
  );
}
