import React from 'react';
import './GetAllCars';

export default function UpdateCar(props) {
  const handleCarData = async () => {
    const url = `http://localhost:5081/Cars/CarById?id=${props.carId}`;
    try {
      const request = await fetch(url, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(props.carData),
      });
      if (!request.ok) {
        console.error(`Hiba a módosítás során (${request.status}): ${request.statusText}`);
        return;
      }
      const response = await request.json();
      props.handleCount();
      console.log(response.message);
    } catch (error) {
      console.error('Hiba történt a módosítás kérésekor:', error);
    }
  };

  return (
    <button type="button" onClick={handleCarData} className="update-button">
      Módosít
    </button>
  );
}
