import React, { useState, useEffect, useCallback } from 'react';
import DeleteCar from './DeleteCar';
import AddNewCar from './AddNewCar';
import './GetAllCars.css';

export default function GetAllCars(props) {
    const url = `http://localhost:5081/cars`;
    const [carsData, setCarsData] = useState([]);
    const [carToUpdate, setCarToUpdate] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [refreshCount, setRefreshCount] = useState(0); // Saját refresh számláló

    const fetchCars = useCallback(async () => {
        setLoading(true);
        setError(null);
        try {
            const request = await fetch(url, {
                headers: {
                    'Content-Type': 'application/json',
                },
            });
            if (!request.ok) {
                throw new Error(`HTTP error! status: ${request.status}`);
            }
            const response = await request.json();
            setCarsData(response.result);
            console.log(response.message);
        } catch (e) {
            setError(e.message);
            console.error("Error fetching cars:", e);
        } finally {
            setLoading(false);
        }
    }, [url]);

    useEffect(() => {
        fetchCars();
    }, [fetchCars, refreshCount]); // Frissítés a saját számláló változásakor

    const handleEditCar = (carFromCard) => {
        setCarToUpdate(carFromCard);
    };

    const handleRefresh = useCallback(() => {
        setRefreshCount(prevCount => prevCount + 1);
        setCarToUpdate(null); // Töröljük a szerkesztendő autót frissítés után
    }, []);

    const carElements = carsData.map((car) => (
        <div
            onDoubleClick={() => handleEditCar(car)}
            className='card m3 pt-2'
            key={car.id}
            style={{ width: 200, float: 'left' }}
        >
            <h3>{car.brand}</h3>
            <h3>{car.type}</h3>
            <h3>{car.color}</h3>
            <h3>{car.myear}</h3>
            <div>
                <DeleteCar carId={car.id} handleCount={handleRefresh} />
            </div>
        </div>
    ));

    return (
        <>
            <AddNewCar handleCount={handleRefresh} carObj={carToUpdate} handleCarObj={handleEditCar} />

            {loading && <p>Loading cars...</p>}
            {error && <p>Error loading cars: {error}</p>}
            <div>{carElements}</div>
        </>
    );
}
