import React, { useState, useEffect } from 'react';
import './GetAllCars.css';

export default function UpdateCar({ carObj, handleCount, cancelEdit }) {
  const [carData, setCarData] = useState({ brand: '', type: '', color: '', myear: '' });

  useEffect(() => {
    if (carObj) {
      setCarData({
        brand: carObj.brand || '',
        type: carObj.type || '',
        color: carObj.color || '',
        myear: carObj.myear ? new Date(carObj.myear).toISOString().split('T')[0] : '',
      });
    }
  }, [carObj]);

  const handleChange = (event) => {
    const { name, value } = event.target;
    setCarData({ ...carData, [name]: value });
  };

  const handleUpdate = async (event) => {
    event.preventDefault();

    try {
      const request = await fetch(`http://localhost:5000/cars/${carObj.id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(carData),
      });

      if (!request.ok) throw new Error(`Hiba: ${request.statusText}`);

      handleCount(); // Lista frissítése
      cancelEdit(); // Kilépés a szerkesztésből
    } catch (error) {
      console.error('Hiba:', error);
    }
  };

  return (
    <div className="add-new-car-container">
      <h2>Autó szerkesztése</h2>
      <form onSubmit={handleUpdate} className="car-form">
        <div className="form-group">
          <label htmlFor="brand">Márka</label>
          <input type="text" id="brand" name="brand" value={carData.brand} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label htmlFor="type">Típus</label>
          <input type="text" id="type" name="type" value={carData.type} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label htmlFor="color">Szín</label>
          <input type="text" id="color" name="color" value={carData.color} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label htmlFor="myear">Gyártási év</label>
          <input type="date" id="myear" name="myear" value={carData.myear} onChange={handleChange} required />
        </div>
        <button type="submit" className="btn btn-primary">Mentés</button>
        <button type="button" className="btn btn-secondary" onClick={cancelEdit}>Mégse</button>
      </form>
    </div>
  );
}
