import React, { useState } from "react";

export default function UpdateCar({ car, onUpdateComplete }) {
  const [formData, setFormData] = useState({
    brand: car.brand,
    type: car.type,
    color: car.color,
    myear: new Date(car.myear).toISOString().split("T")[0], // formázás dátumhoz
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`http://localhost:5000/cars/carbyid?id=${car.id}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) throw new Error("Hiba történt a frissítés során!");

      onUpdateComplete();
    } catch (error) {
      console.error("Frissítési hiba:", error);
    }
  };

  return (
    <div className="update-car-modal">
      <h2>Autó módosítása</h2>
      <form onSubmit={handleSubmit}>
        <label>Márka:</label>
        <input type="text" name="brand" value={formData.brand} onChange={handleChange} required />

        <label>Típus:</label>
        <input type="text" name="type" value={formData.type} onChange={handleChange} required />

        <label>Szín:</label>
        <input type="text" name="color" value={formData.color} onChange={handleChange} required />

        <label>Évjárat:</label>
        <input type="date" name="myear" value={formData.myear} onChange={handleChange} required />

        <button type="submit">Mentés</button>
        <button type="button" onClick={onUpdateComplete}>Mégse</button>
      </form>
    </div>
  );
}
