// components/GetAllCars.js
import React, { useState, useEffect } from 'react';
import DeleteCar from './DeleteCar';
import AddNewCar from './AddNewCar';
import UpdateCar from './UpdateCar'; // Import UpdateCar
import './GetAllCars.css';

export default function GetAllCars(props) {
  const url = `http://localhost:5000/cars`;
  const [carsData, setCarsData] = useState([]);
  const [carToUpdate, setCarToUpdate] = useState(null);

  useEffect(() => {
    const fetchCars = async () => {
      try {
        const request = await fetch(url, {
          headers: {
            'Content-Type': 'application/json',
          },
        });
        if (!request.ok) {
          console.error(`Hiba a lekérdezés során (${request.status}): ${request.statusText}`);
          return;
        }
        const response = await request.json();
        setCarsData(response); // Assuming the backend returns an array directly
        console.log("Autók sikeresen lekérdezve:", response);
      } catch (error) {
        console.error('Hiba történt az autók lekérdezésekor:', error);
      }
    };

    fetchCars();
  }, [props.refresh, url]);

  const handleEditCar = (car) => {
    setCarToUpdate(car);
  };

  const handleUpdateComplete = () => {
    setCarToUpdate(null);
    props.onCarUpdated(); // Trigger refresh in App
  };

  const handleDeleteComplete = () => {
    props.onCarDeleted(); // Trigger refresh in App
  };

  return (
    <div className="get-all-cars-container">
      <AddNewCar onCarAdded={props.onCarAdded} carToUpdate={carToUpdate} onUpdateComplete={handleUpdateComplete} />

      {carToUpdate && (
        <UpdateCar car={carToUpdate} onUpdateComplete={handleUpdateComplete} />
      )}

      <div className="car-list">
        {carsData.map((car) => (
          <div onDoubleClick={() => handleEditCar(car)} className="car-card" key={car._id}>
            <h3>{car.brand}</h3>
            <p>{car.type}</p>
            <div className="car-details">
              <span>Szín: {car.color}</span>
              <span>Évjárat: {new Date(car.myear).toLocaleDateString()}</span>
            </div>
            <div className="car-actions">
              <DeleteCar carId={car._id} onDeleteComplete={handleDeleteComplete} />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
